import * as React from 'react';

const useConditionalEffect = (cb: () => void, then: boolean) => {
	const ref = React.useRef<() => void>(cb);

	React.useEffect(() => {
		if (then) {
			ref.current();
		}
	}, [then]);
};

export default useConditionalEffect;
