import * as React from 'react';
// import { styled } from '../../stitches.config';
import {
  GameBoard,
  GameHeader,
  GamePlate,
  GameStart,
} from '../../components';
import useGame from './useGame';
import useRequestAnimationFrame from './useRequestAnimationFrame';

const Game: React.FC = () => {
  const [state, dispatch] = useGame();

  const isStarted = React.useMemo(() => state.isStarted, [state]);
  const isGameOver = React.useMemo(() => state.isGameOver, [state]);
  const stage = React.useMemo(() => state.stage, [state]);

  const handleClickStart = React.useCallback(() => {
    dispatch({ type: 'init' });
  }, [dispatch]);

  if (!isStarted) {
    return (
      <GamePlate>
        <GameStart onClick={handleClickStart} />
      </GamePlate>
    );
  }

  return (
    <GamePlate>
      <GameHeader
        isStarted={isStarted}
        stage={stage}
        score={state.score}
        onGameEnd={() => dispatch({ type: 'gameover' })}
      />
      <GameBoard stage={stage} />
    </GamePlate>
  );
};

export default Game;
