import * as React from 'react';
import { styled } from '../stitches.config';
import GameStart from './GameStart';
import GameStatus from './GameStatus';

interface GameHeaderProps {
	isStarted: boolean;
	isGameOver: boolean;
	stage: number;
	time: number;
	score: number;
	onClickStart: () => void;
}

const GameHeader: React.FC<GameHeaderProps> = ({
	isStarted,
	isGameOver,
	stage,
	time,
	score,
	onClickStart,
}) => {
	if (!isStarted && isGameOver) {
		return (
			<Header>
				GAMEOVER
			</Header>
		);
	}

	if (!isStarted && !isGameOver) {
		return (
			<Header>
				<GameStart onClick={onClickStart} />
			</Header>
		);
	}

	return (
		<Header>
			<GameStatus
			  stage={stage}
				score={score}
				time={time}
			/>
		</Header>
	);
};

export default GameHeader;

const Header = styled('div', {
	width: 286,
	display: 'flex',
	alignItems: 'center',
	justifyContent: 'space-between',
	marginBottom: 16,
	minHeight: 44.8,
});
