import * as React from 'react';

const initialState: State = {
  remainingTime: 0,
  stage: 0,
  score: 0,
  isStarted: false,
  isGameOver: false,
};

type UseGameReturn = [
  state: State,
  dispatch: React.Dispatch<Action>,
];

const useGame = (): UseGameReturn => {
  const [state, dispatch] = React.useReducer(gameReducer, initialState);

  React.useEffect(() => {
    if (process.env.NODE_ENV === 'development') {
      console.log(state);
    }
  }, [state]);

  return [state, dispatch];
}

export default useGame;

const INITIAL_TIME = 15000;
const DEDUCT_TIME = 3000;

export const gameReducer: React.Reducer<State, Action> = (state, action) => {
  switch (action.type) {
    case 'init': {
      return {
        remainingTime: INITIAL_TIME,
        stage: 1,
        score: 0,
        isStarted: true,
        isGameOver: false,
      };
    }
    case 'done': {
      return initialState;
    }
    case 'tick': {
      if (state.isStarted) {
        if (state.remainingTime < action.payload) {
          return produce(state, { remainingTime: 0, isGameOver: true });
        }

        const remainingTime = Math.max(Math.floor(state.remainingTime - action.payload), 0);
        return produce(state, { remainingTime });
      }

      return state;
    }
    case 'nextStage': {
      return produce(state, {
        remainingTime: INITIAL_TIME,
        stage: state.stage + 1,
        score: Math.pow(state.stage, 3) * state.remainingTime,
      });
    }
    case 'deduct': {
      if (state.remainingTime < DEDUCT_TIME) {
        return produce(state, { remainingTime: 0, isGameOver: true });
      }

      return produce(state, {
        remainingTime: state.remainingTime - DEDUCT_TIME,
      });
    }
    default: {
      throw new Error('invalid action');
    }
  };
};

interface State {
  remainingTime: number;
  stage: number;
  score: number;
  isStarted: boolean;
  isGameOver: boolean;
}

type Action =
  | { type: 'init' }
  | { type: 'done' }
  | { type: 'tick', payload: number }
  | { type: 'nextStage' }
  | { type: 'deduct' }
;

const produce = <S>(state: S, newState: Partial<S>): S =>
  ({ ...state, ...newState });
