import * as React from 'react';
import { GameHeader, GameStatusInfo } from '../../components';
import useRequestAnimationFrame from './useRequestAnimationFrame';

interface StatusProps {
  isStarted: boolean;
  stage: number;
  score: number;
  onGameEnd: () => void;
}

const Status: React.FC<StatusProps> = ({
  isStarted,
  stage,
  score,
  onGameEnd,
}) => {
  const [remainingTime, setRemainingTime] = React.useState<number>(15000);
  const tick = () => setRemainingTime((prev) => prev - 100);

  const elapsedTime = React.useRef<number>(0);

  useRequestAnimationFrame((deltaTime) => {
    if (!isStarted) return;

    elapsedTime.current += deltaTime;

    if (elapsedTime.current >= 100) {
      elapsedTime.current = 0;
      tick();
    }
  }, [isStarted]);

  const once = React.useRef<boolean>(false);

  React.useEffect(() => {
    if (remainingTime >= 0) return;

    onGameEnd();
  }, [remainingTime, onGameEnd]);

  return (
    <GameHeader>
      <GameStatusInfo
        stage={stage}
        score={score}
        time={Math.max(remainingTime, 0)}
      />
    </GameHeader>
  );
};

export default Status;
