import * as React from 'react'
import { GameBoard } from '../../components';
import {
  getItemCount,
  getRandomNumber,
  createGameColors,
} from './utils';

interface ClickableItem {
  key: string;
  background: string;
  isAnswer: boolean;
}

const generateItems = (count: number): ClickableItem[] => {
  const { base, answer } = createGameColors();

  const answerIdx = getRandomNumber(count);

  return Array(count)
    .fill(undefined)
    .map((_, idx) => ({
      key: `${count}-${idx}`,
      background: answerIdx === idx
        ? answer.toString()
        : base.toString(),
      isAnswer: answerIdx === idx,
    }));
};

interface BoardProps {
  stage: number;
  onClickAnswer: () => void;
  onClickWrongAnswer: () => void;
}

const Board: React.FC<BoardProps> = ({
  stage,
  onClickAnswer,
  onClickWrongAnswer,
}) => {
  const itemCount = getItemCount(stage);
  const gridItemCount = Math.floor(Math.sqrt(itemCount));

  return (
    <GameBoard
      columnCount={gridItemCount}
      rowCount={gridItemCount}
    >
      {generateItems(itemCount).map(({
        key,
        background,
        isAnswer,
      }) => (
        <div
          key={key}
          style={{ background }}
          onClick={isAnswer
            ? onClickAnswer.bind(null)
            : onClickWrongAnswer.bind(null)}
        />
      ))}
    </GameBoard>
  );
};

export default React.memo(Board);
