import * as React from 'react';
import { styled } from '../stitches.config';
import FormattedItemTypo from './FormattedItemTypo';

interface GameHeaderProps {
	isStarted: boolean;
	isGameOver: boolean;
	stage: number;
	time: number;
	score: number;
}

const GameHeader: React.FC<GameHeaderProps> = ({
	isStarted,
	isGameOver,
	stage,
	time,
	score,
}) => {
	return (
		<Header>
			<StageTypo>{stage}</StageTypo>
			<div>
				<FormattedItemTypo
					prefix={'score'}
					seperator={':'}
					suffix={score}
				/>
				<FormattedItemTypo
					prefix={'time'}
					seperator={':'}
					suffix={time}
				/>
			</div>
		</Header>
	);
};

export default GameHeader;

const Header = styled('div', {
	width: 286,
	display: 'flex',
	alignItems: 'center',
	justifyContent: 'space-between',
	marginBottom: 16,
});

const StageTypo = styled('span', {
	fontFamily: '$default',
	fontSize: 32,
	fontWeight: '$2',
	lineHeight: 1.4,
	color: '$veryPeri',
	textTransform: 'uppercase',
});
