import * as React from 'react';
import { styled } from '../../stitches.config';
import { FormattedItemTypo } from '../../components';

interface GameHeaderProps {
	showInfo: boolean;
	stage: number;
	time: number;
	score: number;
}

const GameHeader: React.FC<GameHeaderProps> = ({
	showInfo,
	stage,
	time,
	score,
}) => {
	return (
		<Header>
			<FormattedItemTypo
			  prefix={'score'}
				seperator={':'}
				suffix={score}
			/>
			<FormattedItemTypo
			  prefix={'time'}
				seperator={':'}
				suffix={time}
			/>
		</Header>
	);
};

export default GameHeader;

const Header = styled('div', {
	width: 286,
	display: 'flex',
	flexDirection: 'column',
	marginBottom: 16,
});

const Typo = styled('span', {
	fontFamily: '$default',
	fontSize: '$2',
	fontWeight: '$2',
	lineHeight: 1.4,
	color: '$veryPeri',
	textTransform: 'uppercase',
});
